mapper.select:
select
  user_id, username, password, phone, email, Id_card, user_state
from user


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and user_state = #{userState}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and user_state = #{userState}
