mapper.select:
select
  username, password, phone, email, Id_card, state, id
from userinfo_only


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and state = #{state}


        and id = #{id}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from userinfo_only


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and state = #{state}


        and id = #{id}
