mapper.select:
select
id, username, password, phone, email, Id_card, state
from userinfo_only


        and uid = #{uid}


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and state = #{state}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from userinfo_only


        and uid = #{uid}


        and username = #{username}


        and password = #{password}


        and phone = #{phone}


        and email = #{email}


        and Id_card = #{idCard}


        and state = #{state}
